//format currency integer to $USD
const formatter = new Intl.NumberFormat('en-US', {
  style: 'currency',
  currency: 'USD',
  minimumFractionDigits: 2
});

let currency = totalPurchasePrice;
let number = Number(currency.replace(/[^0-9\.-]+/g,""));
console.log(number);

export { formatter }
